import { useState, useEffect } from 'react';
import { Timer, Play, Pause, RotateCcw, Coffee } from 'lucide-react';

const WORK_TIME = 25 * 60; // 25 minutes in seconds
const SHORT_BREAK = 5 * 60; // 5 minutes
const LONG_BREAK = 15 * 60; // 15 minutes
const POMODOROS_BEFORE_LONG_BREAK = 4;

export function PomodoroTimer() {
  const [timeLeft, setTimeLeft] = useState(WORK_TIME);
  const [isRunning, setIsRunning] = useState(false);
  const [mode, setMode] = useState<'work' | 'short-break' | 'long-break'>('work');
  const [completedPomodoros, setCompletedPomodoros] = useState(0);

  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (isRunning && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft(time => time - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      if (mode === 'work') {
        const newCompleted = completedPomodoros + 1;
        setCompletedPomodoros(newCompleted);
        if (newCompleted % POMODOROS_BEFORE_LONG_BREAK === 0) {
          setMode('long-break');
          setTimeLeft(LONG_BREAK);
        } else {
          setMode('short-break');
          setTimeLeft(SHORT_BREAK);
        }
      } else {
        setMode('work');
        setTimeLeft(WORK_TIME);
      }
      setIsRunning(false);
      
      // Play notification sound
      const audio = new Audio('data:audio/wav;base64,');
      audio.play();
    }

    return () => clearInterval(interval);
  }, [isRunning, timeLeft, mode, completedPomodoros]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
  };

  const reset = () => {
    setIsRunning(false);
    setMode('work');
    setTimeLeft(WORK_TIME);
    setCompletedPomodoros(0);
  };

  const getProgressColor = () => {
    switch (mode) {
      case 'work':
        return 'bg-red-500';
      case 'short-break':
        return 'bg-green-500';
      case 'long-break':
        return 'bg-blue-500';
    }
  };

  const getMaxTime = () => {
    switch (mode) {
      case 'work':
        return WORK_TIME;
      case 'short-break':
        return SHORT_BREAK;
      case 'long-break':
        return LONG_BREAK;
    }
  };

  const progress = (timeLeft / getMaxTime()) * 100;

  return (
    <div className="space-y-6">
      <div className="relative h-64 bg-gray-100 dark:bg-gray-700 rounded-lg overflow-hidden">
        <div
          className={`absolute bottom-0 left-0 right-0 transition-all duration-1000 ${getProgressColor()}`}
          style={{ height: `${progress}%` }}
        />
        
        <div className="absolute inset-0 flex flex-col items-center justify-center">
          <div className="text-6xl font-bold mb-4">{formatTime(timeLeft)}</div>
          <div className="text-xl">
            {mode === 'work' ? 'Arbeid' : mode === 'short-break' ? 'Kort pause' : 'Lang pause'}
          </div>
        </div>
      </div>

      <div className="flex justify-center gap-4">
        <button
          onClick={() => setIsRunning(!isRunning)}
          className={`p-4 rounded-full transition-all ${
            isRunning
              ? 'bg-red-500 hover:bg-red-600'
              : 'bg-green-500 hover:bg-green-600'
          } text-white`}
        >
          {isRunning ? <Pause className="w-6 h-6" /> : <Play className="w-6 h-6" />}
        </button>
        <button
          onClick={reset}
          className="p-4 rounded-full bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 transition-all"
        >
          <RotateCcw className="w-6 h-6" />
        </button>
      </div>

      <div className="flex justify-center gap-2">
        {Array.from({ length: POMODOROS_BEFORE_LONG_BREAK }).map((_, i) => (
          <div
            key={i}
            className={`w-3 h-3 rounded-full transition-all ${
              i < completedPomodoros % POMODOROS_BEFORE_LONG_BREAK
                ? 'bg-red-500'
                : 'bg-gray-200 dark:bg-gray-700'
            }`}
          />
        ))}
      </div>

      <div className="grid grid-cols-3 gap-4">
        <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded-lg text-center">
          <Timer className="w-5 h-5 mx-auto mb-2" />
          <div className="text-sm font-medium">25 min arbeid</div>
        </div>
        <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded-lg text-center">
          <Coffee className="w-5 h-5 mx-auto mb-2" />
          <div className="text-sm font-medium">5 min pause</div>
        </div>
        <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded-lg text-center">
          <Coffee className="w-5 h-5 mx-auto mb-2" />
          <div className="text-sm font-medium">15 min lang pause</div>
        </div>
      </div>

      <div className="text-center text-sm text-gray-600 dark:text-gray-400">
        Fullf√∏rte pomodoros i dag: {completedPomodoros}
      </div>
    </div>
  );
}